public void dispose().methodBody:
{
    if (treeListener != null)
        tableTree.removeTreeListener(treeListener);
    treeListener = null;
    Table table = tableTree.getTable();
    if (this.column > -1 && this.column < table.getColumnCount()) {
        TableColumn tableColumn = table.getColumn(this.column);
        tableColumn.removeControlListener(columnListener);
    }
    tableTree = null;
    item = null;
    column = -1;
    super.dispose();
}

public void treeCollapsed(TreeEvent e).methodBody:
{
    if (editor == null || editor.isDisposed())
        return;
    editor.setVisible(false);
    e.display.asyncExec(runnable);
}

public void run().methodBody:
{
    if (editor == null || editor.isDisposed())
        return;
    if (TableTreeEditor.this.tableTree.isDisposed())
        return;
    resize();
    editor.setVisible(true);
}

