void resize().methodBody:
{
    if (tableTree.isDisposed())
        return;
    if (item == null || item.isDisposed())
        return;
    Table table = tableTree.getTable();
    int columnCount = table.getColumnCount();
    if (columnCount == 0 && column != 0)
        return;
    if (columnCount > 0 && (column < 0 || column >= columnCount))
        return;
    super.resize();
}

public void setColumn(int column).methodBody:
{
    Table table = tableTree.getTable();
    int columnCount = table.getColumnCount();

    if (columnCount == 0) {
        this.column = (column == 0) ? 0 : -1;
        resize();
        return;
    }
    if (this.column > -1 && this.column < columnCount) {
        TableColumn tableColumn = table.getColumn(this.column);
        tableColumn.removeControlListener(columnListener);
        this.column = -1;
    }
    if (column < 0 || column >= table.getColumnCount())
        return;
    this.column = column;
    TableColumn tableColumn = table.getColumn(this.column);
    tableColumn.addControlListener(columnListener);
    resize();
}

public void treeExpanded(TreeEvent e).methodBody:
{
    if (editor == null || editor.isDisposed())
        return;
    editor.setVisible(false);
    e.display.asyncExec(runnable);
}
